package dsa.strings;

public class LongCorridor {
    // https://leetcode.com/problems/number-of-ways-to-divide-a-long-corridor/
    // Topic : Math , String , Dynamic Programming
    public static int numberOfWays(String corridor) {
        int mod = 1000000007;
        int sCount = 0;
        int pCount = 0;
        long ways = 1;
        for (int i = 0; i < corridor.length(); i++) {
            if (corridor.charAt(i) == 'S') sCount++;
            if (corridor.charAt(i) == 'P' && sCount >= 2 && sCount % 2 == 0 && i <= corridor.length() - 3) {
                pCount++;
                if (corridor.charAt(i + 1) == 'S') {
                    ways = (ways * (pCount + 1)) % mod;
                    pCount = 0;
                }
            }
        }
        if (sCount < 2 || sCount % 2 != 0) return 0;
        if (sCount == 2) return 1;
        return (int) ((ways % mod + mod) % mod);
    }

    public static void main(String[] args) {
        System.out.println(numberOfWays("SSPPSPS"));
        System.out.println(numberOfWays("PPSPSP"));
        System.out.println(numberOfWays(""));
    }
}

